INSERT INTO news VALUES (0, 'admin','Lima Manfaat Olahraga untuk Menjaga Kesehatan Tubuh', STR_TO_DATE('6/26/2017', '%c/%d/%Y'), 'https://cdn-2.tstatic.net/tribunnews/foto/bank/images/fitness-first-jalin-kerjasama-dengan-reebok-selama-dua-tahun_20161028_122431.jpg', '
